// Type declarations for vite-imagetools
declare module "*&format=webp" {
  const src: string;
  export default src;
}

declare module "*&format=avif" {
  const src: string;
  export default src;
}

declare module "*&format=jpeg" {
  const src: string;
  export default src;
}

declare module "*&format=jpg" {
  const src: string;
  export default src;
}

declare module "*&format=png" {
  const src: string;
  export default src;
}

declare module "*.png?*" {
  const src: string;
  export default src;
}

declare module "*.jpg?*" {
  const src: string;
  export default src;
}

declare module "*.jpeg?*" {
  const src: string;
  export default src;
}

declare module "*.webp?*" {
  const src: string;
  export default src;
}

declare module "*.avif?*" {
  const src: string;
  export default src;
}
